% Load image packagepkg load image% clear all variables and close all figuresclear all; close all% Load an image using dialog box function uigetfile% uigetfile function to get the file path and file name[filename, filepath]=uigetfile({"*.png;*.jpg","Choose cameraman image to load"});% load the image using file path and file name put into squared bracked and separated with a comma ,I = imread([filepath,filename]);% Get the histogram of an imagefigure(1)subplot(1,2,1)imshow(I)title('Original image')subplot(1,2,2)imhist(I);title('Histogram of original image')% Apply binarisation without providing thresholdJ = im2bw (I);% Fill holes in region of imageHolesfilled = imfill (J, "holes");figure(5)subplot(1,2,1)imshow(J)title('Basic binarization')subplot(1,2,2)imshow(Holesfilled)title('Hole filled')% Find circle[centers, radii] = imfindcircles (I, [10 50]);% Show resultsfigure(6)imshow(I)viscircles (centers, radii)title('Detected circles')% Find connected pixelcc = regionprops (Holesfilled, 'all');% Outlines the centroid locationscentroids = cat(1, cc.Centroid);figure(7)imshow(I)hold onplot(centroids(:,1), centroids(:,2), 'b*')title('Centroids displayed')hold off% Display the bounding boxes around the group of pixelsboundingbox = bwboundaries(Holesfilled);figure(8)imshow(I)hold onfor k = 1:numel(boundingbox)  plot(boundingbox{k}(:, 2), boundingbox{k}(:, 1), 'r', 'linewidth', 2);endfortitle('Boundaries displayed')hold off